const ExperienceIcons = () => {
  const { slideElems } = useContext(ObserverContext);
  const techList = [
    "HTML / CSS",
    "JavaScript",
    "TypeScript",
    "MongoDB",
    "Express",
    "React",
    "Node.js",
    "Git / GitHub",
    "BootStrap",
    "Mocha.js",
    "Jest",
    "Vitest",
    "Figma",
  ];
  return (
    <div className="wrapper experience__wrapper">
      {techList.map((tech, index) => (
        <ExperienceIcon
          key={tech}
          tech={tech}
          ref={(el) => el && slideElems.current.push(el)}
        >
          {svgElements[index]}
        </ExperienceIcon>
      ))}
    </div>
  );
};

export default ExperienceIcons;
